mapper.select:
select  from activity_record


        and recruitId = #{recruitId}


        and recruitDetailId = #{recruitDetailId}


        and recruitTitle = #{recruitTitle}


        and taskNo = #{taskNo}


        and serviceStartTime = #{serviceStartTime}


        and serviceEndTime = #{serviceEndTime}


        and instructorId = #{instructorId}


        and certificateLevel = #{certificateLevel}


        and name = #{name}


        and instructorItem = #{instructorItem}


        and reserveStatus = #{reserveStatus}


        and signStatus = #{signStatus}


        and status = #{status}


        and serviceDuration = #{serviceDuration}


        and signInTime = #{signInTime}


        and signOutTime = #{signOutTime}


        and exceedServiceDuration = #{exceedServiceDuration}


        and approvalType = #{approvalType}


        and actualServiceDuration = #{actualServiceDuration}


        and approvalComments = #{approvalComments}


        and approvalBy = #{approvalBy}


        and approvalTime = #{approvalTime}


        and approvalId = #{approvalId}



order by id DESC

select count(1) from activity_record


        and recruitId = #{recruitId}


        and recruitDetailId = #{recruitDetailId}


        and recruitTitle = #{recruitTitle}


        and taskNo = #{taskNo}


        and serviceStartTime = #{serviceStartTime}


        and serviceEndTime = #{serviceEndTime}


        and instructorId = #{instructorId}


        and certificateLevel = #{certificateLevel}


        and name = #{name}


        and instructorItem = #{instructorItem}


        and reserveStatus = #{reserveStatus}


        and signStatus = #{signStatus}


        and status = #{status}


        and serviceDuration = #{serviceDuration}


        and signInTime = #{signInTime}


        and signOutTime = #{signOutTime}


        and exceedServiceDuration = #{exceedServiceDuration}


        and approvalType = #{approvalType}


        and actualServiceDuration = #{actualServiceDuration}


        and approvalComments = #{approvalComments}


        and approvalBy = #{approvalBy}


        and approvalTime = #{approvalTime}


        and approvalId = #{approvalId}

select  from activity_record


        and taskNumber like concat('%',#{taskNumber},'%')


        and recruitTitle like concat('%',#{recruitTitle},'%')


        and name like concat('%',#{name},'%')


        and instructorItem =#{instructorItem}


        and signStatus =#{signStatus}


        and status =#{status}


        and serviceStartTime  >=  #{serviceStartTime}


        and serviceEndTime  <=  #{serviceEndTime}

>
