mapper.select:
select  from activity_recruit_info


        and recruitType = #{recruitType}


        and title = #{title}


        and content = #{content}


        and image = #{image}


        and address = #{address}


        and longitude = #{longitude}


        and latitude = #{latitude}


        and matchApproverId = #{matchApproverId}


        and matchApprover = #{matchApprover}


        and courierStationId = #{courierStationId}


        and courierStation = #{courierStation}


        and serviceStartDate = #{serviceStartDate}


        and serviceEndDate = #{serviceEndDate}


        and serviceHours = #{serviceHours}


        and recruitNumber = #{recruitNumber}


        and status = #{status}


        and approvalStatus = #{approvalStatus}


        and showBanner = #{showBanner}


        and placedTop = #{placedTop}


        and recruitStartDate = #{recruitStartDate}


        and recruitEndDate = #{recruitEndDate}


        and approvalComments = #{approvalComments}


        and deptName = #{deptName}


        and deptId = #{deptId}





        order by ${orderBy}


        order by id DESC

select count(1) from activity_recruit_info


        and recruitType = #{recruitType}


        and title = #{title}


        and content = #{content}


        and image = #{image}


        and address = #{address}


        and longitude = #{longitude}


        and latitude = #{latitude}


        and matchApproverId = #{matchApproverId}


        and matchApprover = #{matchApprover}


        and courierStationId = #{courierStationId}


        and courierStation = #{courierStation}


        and serviceStartDate = #{serviceStartDate}


        and serviceEndDate = #{serviceEndDate}


        and serviceHours = #{serviceHours}


        and recruitNumber = #{recruitNumber}


        and status = #{status}


        and approvalStatus = #{approvalStatus}


        and showBanner = #{showBanner}


        and placedTop = #{placedTop}


        and recruitStartDate = #{recruitStartDate}


        and recruitEndDate = #{recruitEndDate}


        and approvalComments = #{approvalComments}


        and deptName = #{deptName}


        and deptId = #{deptId}

select from activity_recruit_info i LEFT JOIN activity_recruit_detail d on i.id=d.recruitId
and i.title like concat('%',#{title},'%')
and i.recruitType=#{recruitType}
and i.createdBy like concat('%',#{createdBy},'%')
and i.recruitStartDate >= #{recruitStartDate}
and i.recruitEndDate <= #{recruitEndDate}
and i.createdTime >= #{beginTime}
and i.createdTime <= #{endTime}
and i.approvalStatus=#{approvalStatus}
and i.status=#{status}
AND i.status != 4 and i.approvalStatus = 1 and i.recruitStartDate > UNIX_TIMESTAMP(NOW())*1000
i.status != 4 and i.approvalStatus = 1 AND i.recruitStartDate < UNIX_TIMESTAMP(NOW())* 1000 AND UNIX_TIMESTAMP(NOW())* 1000 < i.recruitEndDate+24*60*60*1000-1
i.status != 4 and i.approvalStatus = 1 AND i.recruitEndDate+24*60*60*1000-1 < UNIX_TIMESTAMP(NOW())*1000
i.status != 4 and i.approvalStatus = 1
and i.createdId=#{userId}
and i.approvalStatus = 1 and i.status != 4
AND i.approvalStatus = 1 and i.status != 4 AND i.recruitEndDate+24*60*60*1000-1 < UNIX_TIMESTAMP(NOW())*1000
GROUP BY i.id
HAVING max(d.serviceDate+TIME_TO_SEC(d.serviceEndTime)) > UNIX_TIMESTAMP(NOW())*1000
HAVING max(d.serviceDate+TIME_TO_SEC(d.serviceEndTime)) > UNIX_TIMESTAMP(NOW())*1000
HAVING max(d.serviceDate+TIME_TO_SEC(d.serviceEndTime)) < UNIX_TIMESTAMP(NOW())*1000
order by CASE WHEN i.approvalStatus != 1 THEN null WHEN i.`status`!=0 THEN i.`status` WHEN i.approvalStatus = 1 AND i.`status`=0 AND max(d.serviceDate+TIME_TO_SEC(d.serviceEndTime)) < UNIX_TIMESTAMP(NOW())*1000 THEN 3 WHEN i.approvalStatus = 1 AND i.`status`=0 AND i.recruitEndDate+24*60*60*1000-1 < UNIX_TIMESTAMP(NOW())*1000 THEN 2 WHEN i.approvalStatus = 1 AND i.`status`=0 AND i.recruitStartDate < UNIX_TIMESTAMP(NOW())* 1000 AND UNIX_TIMESTAMP(NOW())* 1000 < i.recruitEndDate+24*60*60*1000-1 THEN 1 ELSE i.`status` END ASC,i.placedTop DESC,i.createdTime DESC
order by i.placedTop DESC,i.createdTime DESC

SELECT * FROM ( SELECT a.id, 0 AS 'source', recruitType AS 'type', a.title, a.content, a.recruitStartDate, a.recruitEndDate, a.createdTime, a.approvalStatus AS 'status', a.updateId FROM activity_recruit_info a UNION SELECT n.id, 1 AS 'source', n.type, n.title, n.content, '' AS 'recruitStartDate', '' AS 'recruitEndDate', n.createdTime, n.auditState AS 'status', n.updateId FROM news_notice n ) t
t.status in(
#{status}
)
AND updateId=#{updateId}
ORDER BY t.createdTime DESC

SELECT count(*) FROM ( SELECT a.id, a.approvalStatus AS 'status', a.updateId FROM activity_recruit_info a UNION SELECT n.id, n.auditState AS 'status', n.updateId FROM news_notice n ) t
t.status in(
#{status}
)
AND updateId=#{updateId}
