select count(1) from course_info


        and title = #{title}


        and type = #{type}


        and lecturerTeacherId = #{lecturerTeacherId}


        and courseStartTime = #{courseStartTime}


        and courseEndTime = #{courseEndTime}


        and address = #{address}


        and longitude = #{longitude}


        and latitude = #{latitude}


        and limitNumber = #{limitNumber}


        and signNumber = #{signNumber}


        and signStartTime = #{signStartTime}


        and signEndTime = #{signEndTime}


        and description = #{description}


        and image = #{image}


        and approvalStatus = #{approvalStatus}


        and approvalComments = #{approvalComments}


        and showBanner = #{showBanner}


        and placedTop = #{placedTop}


        and deptName = #{deptName}


        and deptId = #{deptId}


        and state = #{state}


        and approvalId = #{approvalId},


        and approvalBy = #{approvalBy},


        and approvalTime = #{approvalTime},


        and releaseTime = #{releaseTime},


        and offerCourseType = #{offerCourseType},

select from course_info
and title like concat('%',#{title},'%')
and lecturerTeacherName like concat('%',#{lecturerTeacherName},'%')
and type=#{type}
and offerCourseType=#{offerCourseType}
and createdBy like concat('%',#{createdBy},'%')
and createdTime > #{startTime}
and createdTime < #{endTime}+24*60*60*1000-1
and approvalStatus=#{approvalStatus}
and state=#{state}
AND state!=5 AND approvalStatus = 1 AND courseEndTime < UNIX_TIMESTAMP(NOW())*1000
AND state!=5 AND approvalStatus = 1 AND courseStartTime < UNIX_TIMESTAMP(NOW())* 1000 AND UNIX_TIMESTAMP(NOW())* 1000 < courseEndTime
AND state!=5 AND approvalStatus = 1 AND UNIX_TIMESTAMP(NOW())*1000 > signEndTime AND courseStartTime > UNIX_TIMESTAMP(NOW())* 1000
AND state!=5 AND approvalStatus = 1 AND UNIX_TIMESTAMP(NOW())* 1000 > signStartTime AND UNIX_TIMESTAMP(NOW())* 1000 < signEndTime AND courseStartTime > UNIX_TIMESTAMP(NOW())* 1000
AND state!=5 AND (approvalStatus != 1 OR (signStartTime > UNIX_TIMESTAMP(NOW())* 1000 AND courseStartTime > UNIX_TIMESTAMP(NOW())* 1000))
AND approvalStatus=0
AND (approvalStatus=1 or approvalStatus=3)
AND createdId=#{createdId}
AND approvalStatus=1 AND state!=5 AND releaseTime < UNIX_TIMESTAMP(NOW())* 1000
and updateTime > #{approvalStartTime}
and updateTime < #{approvalEndTime}+24*60*60*1000-1
and (signStartTime between #{signStartTime} and #{signEndTime} or signEndTime between #{signStartTime} and #{signEndTime})
and lecturerTeacherId=#{lecturerTeacherId}
and releaseTime >= #{releaseStartTime}
and releaseTime <= #{releaseEndTime}
order by CASE WHEN approvalStatus != 1 THEN null WHEN `state`=5 THEN `state` WHEN approvalStatus = 1 AND courseEndTime < UNIX_TIMESTAMP(NOW())*1000 THEN 4 WHEN approvalStatus = 1 AND courseStartTime < UNIX_TIMESTAMP(NOW())* 1000 AND UNIX_TIMESTAMP(NOW())* 1000 < courseEndTime THEN 3 WHEN approvalStatus = 1 AND signEndTime < UNIX_TIMESTAMP(NOW())*1000 THEN 2 WHEN approvalStatus = 1 AND signStartTime < UNIX_TIMESTAMP(NOW())* 1000 AND UNIX_TIMESTAMP(NOW())* 1000 < signEndTime THEN 0 WHEN approvalStatus = 1 AND signStartTime > UNIX_TIMESTAMP(NOW())* 1000 THEN 1 END ASC ,placedTop DESC,releaseTime DESC
order by createdTime DESC
order by approvalTime DESC
order by placedTop DESC,releaseTime DESC

SELECT a.id, a.title, a.type, a.offerCourseType, a.lecturerTeacherId, a.lecturerTeacherName, SUBSTRING(IFNULL(a.courseStartTime,UNIX_TIMESTAMP(CONCAT(FROM_UNIXTIME(b.courseDate/1000,'%Y-%m-%d'),' ',b.startTime,':00'))*1000),1,13) as courseStartTime, SUBSTRING(IFNULL(a.courseEndTime,UNIX_TIMESTAMP(CONCAT(FROM_UNIXTIME(b.courseDate/1000,'%Y-%m-%d'),' ',b.endTime,':00'))*1000),1,13) as courseEndTime, a.address, a.longitude, a.latitude, a.limitNumber, a.signNumber, a.signStartTime, a.signEndTime, a.description, a.image, a.approvalStatus, a.approvalComments, a.showBanner, a.placedTop, a.deptName, a.deptId, a.createdId, a.createdBy, a.createdTime, a.updateId, a.updateBy, a.updateTime, a.approvalId, a.approvalBy, a.approvalTime, a.releaseTime, a.courseStartZone, a.courseEndZone, -- 课表取消：6 IF(b.cancelStatus = 2,6,a.state) state, b.cancelReason from course_info a LEFT JOIN course_schedule b ON a.id = b.courseId
and a.title like concat('%',#{title},'%')
and a.type=#{type}
and a.offerCourseType=#{offerCourseType}
and a.createdBy like concat('%',#{createdBy},'%')
and a.createdTime > #{startTime}
and a.createdTime < #{endTime}+24*60*60*1000-1
and a.approvalStatus=#{approvalStatus}
and a.state=#{state}
AND a.state!=5 AND a.approvalStatus = 1 AND a.courseEndTime < UNIX_TIMESTAMP(NOW())*1000
AND a.state!=5 AND a.approvalStatus = 1 AND a.courseStartTime < UNIX_TIMESTAMP(NOW())* 1000 AND UNIX_TIMESTAMP(NOW())* 1000 < a.courseEndTime
AND a.state!=5 AND a.approvalStatus = 1 AND UNIX_TIMESTAMP(NOW())*1000 > a.signEndTime AND a.courseStartTime > UNIX_TIMESTAMP(NOW())* 1000
AND a.state!=5 AND a.approvalStatus = 1 AND UNIX_TIMESTAMP(NOW())* 1000 > a.signStartTime AND UNIX_TIMESTAMP(NOW())* 1000 < a.signEndTime AND a.courseStartTime > UNIX_TIMESTAMP(NOW())* 1000
AND a.state!=5 AND (a.approvalStatus != 1 OR (a.signStartTime > UNIX_TIMESTAMP(NOW())* 1000 AND a.courseStartTime > UNIX_TIMESTAMP(NOW())* 1000))
AND a.approvalStatus=0
AND (a.approvalStatus=1 or a.approvalStatus=3)
AND a.createdId=#{createdId}
AND a.approvalStatus=1 AND a.state!=5 AND a.releaseTime < UNIX_TIMESTAMP(NOW())* 1000
and a.updateTime > #{approvalStartTime}
and a.updateTime < #{approvalEndTime}+24*60*60*1000-1
and (a.signStartTime between #{signStartTime} and #{signEndTime} or a.signEndTime between #{signStartTime} and #{signEndTime})
and a.lecturerTeacherId=#{lecturerTeacherId}
and a.releaseTime >= #{releaseStartTime}
and a.releaseTime <= #{releaseEndTime}
order by CASE WHEN a.approvalStatus != 1 THEN null WHEN a.state =5 THEN a.state WHEN a.approvalStatus = 1 AND a.courseEndTime < UNIX_TIMESTAMP(NOW())*1000 THEN 4 WHEN a.approvalStatus = 1 AND a.courseStartTime < UNIX_TIMESTAMP(NOW())* 1000 AND UNIX_TIMESTAMP(NOW())* 1000 < a.courseEndTime THEN 3 WHEN a.approvalStatus = 1 AND a.signEndTime < UNIX_TIMESTAMP(NOW())*1000 THEN 2 WHEN a.approvalStatus = 1 AND a.signStartTime < UNIX_TIMESTAMP(NOW())* 1000 AND UNIX_TIMESTAMP(NOW())* 1000 < a.signEndTime THEN 0 WHEN a.approvalStatus = 1 AND a.signStartTime > UNIX_TIMESTAMP(NOW())* 1000 THEN 1 END ASC ,a.placedTop DESC,a.releaseTime DESC
order by a.placedTop DESC,a.releaseTime DESC


SELECT c.id as courseId,c.title,c.lecturerTeacherId,c.lecturerTeacherName,c.courseStartTime,d.certificateGrade,d.guideProject,ROUND( IF ( c.courseEndTime  >  c.courseStartTime,( c.courseEndTime - c.courseStartTime )/ 1000 / 60,0 )) serviceTimeTotal
FROM
`course_info` c LEFT JOIN course_tearcher d on c.lecturerTeacherId = d.id


        and c.offerCourseType = 1 and c.state = 4

            and c.title = #{title}


            and c.lecturerTeacherName = #{lecturerTeacherName}


            and c.courseStartTime  >=  #{beginTime}


            and c.courseStartTime  <=  #{endTime}



union all

SELECT
    a.courseId,
    b.title,
    b.lecturerTeacherId,
    b.lecturerTeacherName,
    b.courseStartTime,
    d.certificateGrade,
    d.guideProject,
    SUM(IF(ROUND(ROUND( UNIX_TIMESTAMP( CONCAT_WS( '', FROM_UNIXTIME( a.courseDate / 1000, '%Y-%m-%d' ), ' ', a.endTime, ':00' ))* 1000 )/ 1000 / 60 )    >  ROUND(ROUND( UNIX_TIMESTAMP( CONCAT_WS( '', FROM_UNIXTIME( a.courseDate / 1000, '%Y-%m-%d' ), ' ', a.startTime, ':00' ))* 1000 )/ 1000 / 60 ),(
    ROUND(
    ROUND( UNIX_TIMESTAMP( CONCAT_WS( '', FROM_UNIXTIME( a.courseDate / 1000, '%Y-%m-%d' ), ' ', a.endTime, ':00' ))* 1000 )/ 1000 / 60
    ))-(ROUND(
    ROUND( UNIX_TIMESTAMP( CONCAT_WS( '', FROM_UNIXTIME( a.courseDate / 1000, '%Y-%m-%d' ), ' ', a.startTime, ':00' ))* 1000 )/ 1000 / 60
    )),0)) serviceTimeTotal
FROM `course_schedule` a
LEFT JOIN course_info b on a.courseId = b.id
LEFT JOIN course_tearcher d on b.lecturerTeacherId = d.id


        and a.cancelStatus = 1 and  a.courseDate  <  unix_timestamp() * 1000

            and b.title = #{title}


            and b.lecturerTeacherName = #{lecturerTeacherName}


            and b.courseStartTime  >=  #{beginTime}


            and b.courseStartTime  <=  #{beginTime}



GROUP BY a.courseId
