mapper.select:
select  from course_info


        and title = #{title}


        and type = #{type}


        and lecturerTeacherId = #{lecturerTeacherId}


        and lecturerTeacherName = #{lecturerTeacherName}


        and courseStartTime = #{courseStartTime}


        and courseEndTime = #{courseEndTime}


        and address = #{address}


        and longitude = #{longitude}


        and latitude = #{latitude}


        and limitNumber = #{limitNumber}


        and signNumber = #{signNumber}


        and signStartTime = #{signStartTime}


        and signEndTime = #{signEndTime}


        and description = #{description}


        and image = #{image}


        and approvalStatus = #{approvalStatus}


        and approvalComments = #{approvalComments}


        and showBanner = #{showBanner}


        and placedTop = #{placedTop}


        and state = #{state}



order by id DESC

select count(1) from course_info


        and title = #{title}


        and type = #{type}


        and lecturerTeacherId = #{lecturerTeacherId}


        and lecturerTeacherName = #{lecturerTeacherName}


        and courseStartTime = #{courseStartTime}


        and courseEndTime = #{courseEndTime}


        and address = #{address}


        and longitude = #{longitude}


        and latitude = #{latitude}


        and limitNumber = #{limitNumber}


        and signNumber = #{signNumber}


        and signStartTime = #{signStartTime}


        and signEndTime = #{signEndTime}


        and description = #{description}


        and image = #{image}


        and approvalStatus = #{approvalStatus}


        and approvalComments = #{approvalComments}


        and showBanner = #{showBanner}


        and placedTop = #{placedTop}


        and state = #{state}

select  from course_info


        and title liek concat('%',#{title},'%')


        and type=#{type}


        and createdBy like concat('%',#{createdBy},'%')


        and createdTime  >  #{startTime}


        and createdTime  <  #{endTime}


        and state=#{state}


        AND `state` = 1 AND courseEndTime+24*60*60*1000-1  <  UNIX_TIMESTAMP(NOW())*1000


        AND `state` = 1 AND courseStartTime  <  UNIX_TIMESTAMP(NOW())* 1000
        AND UNIX_TIMESTAMP(NOW())* 1000  <  courseEndTime+24*60*60*1000-1


        AND `state` = 1 AND signStartTime+24*60*60*1000-1  <  UNIX_TIMESTAMP(NOW())*1000
        AND `state` = 1 AND courseStartTime  >  UNIX_TIMESTAMP(NOW())* 1000


        AND `state` = 1 AND signStartTime  <  UNIX_TIMESTAMP(NOW())* 1000
        AND UNIX_TIMESTAMP(NOW())* 1000  <  signEndTime+24*60*60*1000-1
        AND `state` = 1 AND courseStartTime  >  UNIX_TIMESTAMP(NOW())* 1000


        AND (`state` != 1 OR signStartTime  >  UNIX_TIMESTAMP(NOW())* 1000)


        AND `state`=0


        AND (`state`=1 or `state`=3)


        AND createdId=#{createdId}


        and updateTime  >  #{approvalStartTime}


        and updateTime  <  #{approvalEndTime}


order by placedTop DESC,createdTime DESC
