mapper.select:
select  from reservation


        and relevanceId = #{relevanceId}


        and type = #{type}


        and source = #{source}


        and workingId = #{workingId}


        and title = #{title}


        and image = #{image}


        and startTime = #{startTime}


        and endTime = #{endTime}


        and address = #{address}


        and longitude = #{longitude}


        and latitude = #{latitude}


        and userId = #{userId}


        and name = #{name}


        and identityNumber = #{identityNumber}


        and phone = #{phone}


        and status = #{status}



order by id DESC

select count(1) from reservation


        and relevanceId = #{relevanceId}


        and type = #{type}


        and source = #{source}


        and workingId = #{workingId}


        and title = #{title}


        and image = #{image}


        and startTime = #{startTime}


        and endTime = #{endTime}


        and address = #{address}


        and longitude = #{longitude}


        and latitude = #{latitude}


        and userId = #{userId}


        and name = #{name}


        and identityNumber = #{identityNumber}


        and phone = #{phone}


        and status = #{status}

select id, relevanceId, type, workingId, title, image, startTime, endTime, address, longitude, latitude, userId, IFNULL(a.`name`,(select GROUP_CONCAT( b.userName SEPARATOR ',' ) from reservation_member b where a.id = b.relevanceId)) as `name`, IFNULL(a.`identityNumber`,(select GROUP_CONCAT( b.identityNumber SEPARATOR ',' ) from reservation_member b where a.id = b.relevanceId)) as `identityNumber`, IFNULL(a.phone,(select GROUP_CONCAT( b.phone SEPARATOR ',' ) from reservation_member b where a.id = b.relevanceId)) as phone, signTime, signType, signStatus, source, STATUS, createdId, createdBy, createdTime, updateId, updateBy, updateTime from reservation a
and `name` like concat('%',#{name},'%')
and identityNumber=#{identityNumber}
and title like concat('%',#{title},'%')
and `type` = #{type}
and status = #{status}
and (status = #{status} or status=2)
and status = #{status}
and createdTime >= #{startTime}
and createdTime <= #{endTime}
and relevanceId=#{relevanceId}
and userId = #{userId}
order by startTime desc,endTime desc

select id, relevanceId, type, workingId, title, image, startTime, endTime, address, longitude, latitude, userId, IFNULL(a.`name`,(select GROUP_CONCAT( b.userName SEPARATOR ',' ) from reservation_member b where a.id = b.relevanceId)) as `name`, IFNULL(a.`identityNumber`,(select GROUP_CONCAT( b.identityNumber SEPARATOR ',' ) from reservation_member b where a.id = b.relevanceId)) as `identityNumber`, IFNULL(a.phone,(select GROUP_CONCAT( b.phone SEPARATOR ',' ) from reservation_member b where a.id = b.relevanceId)) as phone, signTime, signType, signStatus, source, STATUS, createdId, createdBy, createdTime, updateId, updateBy, updateTime from reservation a
and `type` = #{type}
and createdTime >= #{startTime}
and createdTime <= #{endTime}
and relevanceId = #{relevanceId}
and signType = #{signType}
and signStatus = #{signStatus}
order by signTime desc
