mapper.select:
select  from working_schedule


        and activityId = #{activityId}


        and activityTitle = #{activityTitle}


        and activityDetailId = #{activityDetailId}


        and activityType = #{activityType}


        and courierStationId = #{courierStationId}


        and courierStationName = #{courierStationName}


        and scheduleTime = #{scheduleTime}


        and serviceTimeId = #{serviceTimeId}


        and serviceBeginTime = #{serviceBeginTime}


        and serviceEndTime = #{serviceEndTime}


        and remarks = #{remarks}





        order by ${orderBy}


        order by id DESC

select count(1) from working_schedule


        and activityId = #{activityId}


        and activityTitle = #{activityTitle}


        and activityDetailId = #{activityDetailId}


        and activityType = #{activityType}


        and courierStationId = #{courierStationId}


        and courierStationName = #{courierStationName}


        and scheduleTime = #{scheduleTime}


        and serviceTimeId = #{serviceTimeId}


        and serviceBeginTime = #{serviceBeginTime}


        and serviceEndTime = #{serviceEndTime}


        and remarks = #{remarks}

select a.id, a.courierStationName, a.scheduleTime, a.serviceBeginTime, a.serviceEndTime, a.createdBy, a.createdTime, (select GROUP_CONCAT(b.userName) from working_schedule_user b where a.id = b.workingScheduleId and b.type = 1) as stationUserName, (select GROUP_CONCAT(b.userName) from working_schedule_user b where a.id = b.workingScheduleId and b.type = 2) as fixationUserName, (select GROUP_CONCAT(b.userName) from working_schedule_user b where a.id = b.workingScheduleId and b.type = 3) as subscribeUserName from working_schedule a
a.courierStationId = #{courierStationId}
and a.scheduleTime >= #{scheduleBeginTime}
and a.scheduleTime <= #{scheduleEndTime}
and EXISTS (select y.id from working_schedule_user y where a.id = y.workingScheduleId and y.userName like CONCAT('%',#{userName},''))
order by id desc

select a.address, a.longitude, a.latitude, b.id, b.scheduleTime, FROM_UNIXTIME(b.scheduleTime/1000,'%Y-%m-%d') as scheduleTimeText, b.serviceBeginTime, b.serviceEndTime, b.courierStationId, b.activityType, b.activityId, b.activityTitle, c.signInTime, c.signOutTime, c.serviceTime, c.id as workingScheduleUserId from working_schedule b INNER JOIN working_schedule_user c on c.workingScheduleId = b.id
INNER JOIN courier_station a on b.courierStationId = a.id
INNER JOIN activity_recruit_info a on b.activityId = a.id
where c.reserveStatus = 0 and b.activityType = #{activityType} and c.id = #{workingScheduleUserId}
and b.scheduleTime between #{scheduleBeginTime} and #{scheduleEndTime}
and c.userId = #{userId}
ORDER BY b.scheduleTime,b.serviceBeginTime

select b.id, b.scheduleTime, FROM_UNIXTIME(b.scheduleTime/1000,'%Y-%m-%d') as scheduleTimeText, b.serviceBeginTime, b.serviceEndTime, b.courierStationId, b.activityType, b.activityId, b.activityTitle, c.signInTime, c.signOutTime, c.serviceTime, c.id as workingScheduleUserId from working_schedule b INNER JOIN working_schedule_user c on c.workingScheduleId = b.id where c.reserveStatus = 0 and c.id = #{workingScheduleUserId}
and b.scheduleTime between #{scheduleBeginTime} and #{scheduleEndTime}
and c.userId = #{userId}
